import React, { useEffect, useState } from "react";
import axios from "axios";
import { Article } from "./components/Article/Article";
import { Navbar } from "./components/Navbar/Navbar";
/* import { Counter } from "./components/Counter/Counter";
 */
import article1Img from "./assets/images/article1.jpeg";
import article2Img from "./assets/images/article2.jpeg";
import article3Img from "./assets/images/article3.jpeg";
import article4Img from "./assets/images/article4.jpeg";


import "./styles/App.css";
// Componente em classe é uma classe que herda a classee Component do React, e retorna HTML dentro do método render.

// Componente funcional é uma função que retorna HTML.

function App () {
  const [news, setNews] = useState([]);

  useEffect(() => {
    async function loadNews() {
      const response = await axios.get('https://api.spaceflightnewsapi.net/v3/articles')
      const newsData = response.data;

      console.log(newsData);
      setNews(newsData);
    }

    loadNews();
  },[])

  // Método responsável por renderizar o conteúdo HTML do nosso componente.
      return (
      <>
        <Navbar />

        <section id="articles">
          {news.map((article) => {
            <Article
              title={article.title}
              provider={article.newsSite}
              description={article.summary}
              thumbnail={article1Img}
            />

          })}

        </section>
      </>
    );
  }


export default App;
